document.addEventListener(
  "DOMContentLoaded",
  function () {
    var t = document.querySelectorAll("[data-bss-dynamic-product]");
    for (var e of t) {
      var n = e.dataset.bssDynamicProductParam;
      e.dataset.reflowProduct = new URL(location.href).searchParams.get(n);
    }
  },
  !1
),
  document.addEventListener("DOMContentLoaded", function () {
    new Swiper(".swiper-container", {
      loop: !0,
      pagination: { el: ".swiper-pagination", clickable: !0 },
      paginationClickable: !0,
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
    });
  }),
  $(document).ready(function () {
    $(".search").keyup(function () {
      var t = $(".search").val(),
        e =
          ($(".results tbody").children("tr"),
          t.replace(/ /g, "'):containsi('"));
      $.extend($.expr[":"], {
        containsi: function (t, e, n, r) {
          return (
            (t.textContent || t.innerText || "")
              .toLowerCase()
              .indexOf((n[3] || "").toLowerCase()) >= 0
          );
        },
      }),
        $(".results tbody tr")
          .not(":containsi('" + e + "')")
          .each(function (t) {
            $(this).attr("visible", "false");
          }),
        $(".results tbody tr:containsi('" + e + "')").each(function (t) {
          $(this).attr("visible", "true");
        });
      var n = $('.results tbody tr[visible="true"]').length;
      $(".counter").text(n + " item"),
        "0" == n ? $(".no-result").show() : $(".no-result").hide();
    });
  });
